import '../styles/PostBox.css';
import {useEffect} from "react";
import propic from '../img/propic.jpg';

function PostBox() {
    useEffect(() => {
        const textarea = document.getElementById('floatingTextarea');
        textarea.addEventListener('input', resizeTextarea);
    })
    return(
        <div className='container-fluid'>
            <div className='row rounded-5 bg-white p-0'>
                <div className='col-2 p-4'>
                    <img src={propic} className='w-100' id='propic'/>
                </div>
                <div className='col-10'>

                    <div className="form-floating">
                        <textarea className="form-control textarea-input" id="floatingTextarea" placeholder="Your message..."/>
                        <label htmlFor="floatingTextarea">What's you want to squeal?</label>
                    </div>
                </div>
            </div>
        </div>
    );
}

function resizeTextarea() {
    const textarea = document.getElementById('floatingTextarea');
    textarea.style.height = 'auto';
    textarea.style.height = `${textarea.scrollHeight}px`;
}

export default PostBox;
